/* CSS Variables */
:root {
    --accent: rgb(231, 65, 25);
    --primary: rgb(234, 212, 103);
    --primarydark: rgb(135, 62, 13);
    --secondary: rgb(235, 227, 208);
}

body, html {
    margin: 0px;
    overflow-x: hidden; 
}

#root {
    background-color: #ddd;
    font-family: 'Mukta', sans-serif;
    margin: 0;
    display: grid;
    grid-template-areas: 
    "landing"
    "navigation"
    "promo"
    "menu"
    "footer";
    grid-template-rows: auto repeat(4, min-content);
}

#landing {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 1500px;
    width: 100vw;
    background: url(ecb1f0e05ec1e271b72f785307a9e6e6.jpg);
    background-attachment: fixed;
    background-position: 50% 0%;
    background-repeat: no-repeat;
    background-size: cover;
}

#landing > h1 {
    text-transform: uppercase;
    margin: 0;
    padding: 0;
}

#landing > a {
    position: fixed;
    bottom: 0.5%;
    left: 50%;
    color: var(--accent);
    background-color: rgba(234, 212, 103, 0.6);
    padding: 8px 12px 2px;
    border-radius: 10px;
    border: 2px solid var(--accent);
    transition: all 2s ease;
}

#hor > h1 {
    font-size: 5rem;
    margin: 0px;
}

#hor > p {
    margin: 0px;
    font-size: 1.5rem;
}

#hours {
    text-align: center;
    background-color: var(--secondary);
    border-bottom: 5px double var(--accent);
    border-top: 5px double var(--accent);
}
#hours-list {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
}

.hours-detail {
    border: 2px solid black;
    margin: 48px;
    /* width: min-content;
    height: min-content; */
}

.hours-detail > h1 {
    margin: 0;
}

.hours-detail > h4, .hours-detail > h5, .hours-detail > h6 {
    font-size: 3rem;
    margin: 0px;
}

