#info > #connect {
    display: flex;
    justify-content: space-evenly;
}

#navigation {
    background-color: var(--accent);
    position: sticky;
    top: 0;
    left: 0;
    z-index: 1;
}

#navigation > ul {
    display: grid;
    grid-template-columns: repeat(2,1fr);
    margin: 0;
    padding: 0;
}

#navigation > ul > li {
    list-style: none;
    display: flex;
    justify-content: center;
    font-size: 1.5rem;
    text-transform: uppercase;
    letter-spacing: 2px;
}

#navigation a:link {
    text-decoration: none;
}

#navigation a, #navigation li {
    color: var(--primary);
}

#navigation a:hover, #navigation li:hover {
    color: var(--primarydark);
}

#promo {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 1000px;
    width: 100vw;
    background: url(98823c32589a0da2ae2e7b74c4bdc6ba.jpg);
    background-position: 0% 50%;
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: cover;
}

#disclaimer {
    background-color: rgba(0,0,0,0.2);
    border: 3px solid black;
    padding: 0px 16px;
    color: var(--primary);
}

#promotion {
    text-align: center;
    border-top: 5px dotted black;
    border-bottom: 5px dotted black;
    padding-bottom: 9px;
}
#promotion > h1 {
    color: white;
    font-family: 'Abril Fatface';
    font-size: 5rem;
    text-align: center;
    margin: 0px;
    padding: 0px;
    letter-spacing: 20px;
}

#promotion > p {
    font-family: 'Lobster', cursive;
    font-size: 2rem;
    margin: 0px;
}

#promotion > h3 {
    font-size: 2rem;
    color: var(--primary);
    letter-spacing: 1.5px;
    margin: 0px;
}

#promotion > h4 {
    color: var(--primary);
    font-family: 'Lobster', 'Courier New', Courier, monospace;
    font-weight: 100;
    margin: 0px;
    letter-spacing: 4px;
/*     background-color: rgba(255,255,255,0.2); */
}

#promotion > h3:hover {
    border: 3px solid black;
}

#promotion > h2 {
    color: var(--accent);
}


#menuitems {
    display: grid;
    grid-template-rows: repeat(3, auto)
}

#stepone {
    display: grid;
    grid-template-rows: min-content auto;
}

#steponegrid {
    display: grid;
    grid-template-columns: 15% auto;
}

#steponegrid > h4 {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 2rem;
}

#steponegrid > ul {
    display: grid;
    grid-gap: 10px;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: min-content auto;
}

#steponegrid > ul > img {
    width: 100%;
}

#steponegrid > ul > li {
    list-style: none;
    text-align: center;
}

.steps > h2 {
    display: flex;
    justify-content: center;
}

#steptwo {
    display: grid;
    grid-template-rows: repeat(3,min-content);
}

#steptwo > ul > li {
    list-style: none;
    text-align: center;
}

#steptwo > ul {
    display: grid;
    grid-template-columns: 1fr 1fr;
}

#steptwo > #steptwogrid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-gap: 16px;
    margin: 16px;
}

#steptwogrid > img {
    display: flex;
    justify-content: center;
    width: 100%;
    height: 300px;
}

#menupic {
    display: flex;
    justify-content: center;
}

.fa-facebook {
    color: rgb(59,89,152);
}

.fa-phone-square {
    color: rgb(20, 238, 57);
}

strong {
    transition: 0.65s font-size ease, 0.50s opacity ease;
    opacity: 0.1;
}

strong:hover {
    opacity: 1;
}


#meat-volume, #sauce-selections {
    background-color: #ddd;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    text-align: center;
}

.meat-volume-divs, .sauce-divs, .meat-selection-divs {
    display: flex;
    align-items: center;
    flex-direction: column;
}

#header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 16px;
}

img {
    height: 200px;
    width: 200px;
}

#meat-selections {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
}

#order {
    margin: 16px;
}

#order-component {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-areas: 'current-order' 'order';
} 

.meal-order {
    border: 1px solid black;
    padding: 0px 8px;
}
.meal-order p,
.meal-order h4 {
    display: inline;
    margin: 0;
}

/** 
Order 1 Page
*/
.card {
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    background-color: white;
    text-align: center;
}
.card:hover {
    box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    background-color: #eee;
    padding: 12px;
}
.content {
    padding: 2px 16px;
}
.container-meal, #container-sauce {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 18px;
    padding: 18px;
}
#container-meat {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 18px;
    padding: 18px;
}
#container h1{
    text-align: center;
}
#order-container{
    display: flex;
    justify-content: center;
}
#container {
    width: 50vw;
}

#border{
    border: 2px solid black;
}

#footer {
    display: grid;
    grid-template-rows: auto min-content; 
}

#footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    background-color: var(--secondary);
    padding: 0px 36px;
}

#footer-bottom > div {
    display: flex;
    justify-content: space-around;
    width: auto;
}
